include 'db.inc.php';

$db=mysql_connect(MYSQL_HOST,MYSQL_USER,MYSQL_PASSWORD) or die('unable to connect . ');

mysql_select_db(MYSQL_DB,$db) or die(mysql_error($db));

//creating user table

$query='CREATE TABLE IF NOT EXISTS site_user(
        user_id     INTEGER     NOT NULL       AUTO_INCREMENT,
        username    VARCHAR(20) NOT NULL,
        password    CHAR(41)    NOT NULL,
        
        PRIMARY KEY(user_id)
        
)ENGINE=MyISAM';
mysql_query($query,$db) or die(mysql_error($db));
echo 'site table created';
//CREATE THE USER INFORMATION TABLE

$query='CREATE TABLE IF NOT EXISTS site_user_info(
        user_id         INTEGER         NOT NULL,
        first_name      VARCHAR(20)     NOT NULL,
        last_name       VARCHAR(20)     NOT NULL,
        email           VARCHAR(20)     NOT NULL,
        
        FOREIGN KEY(user_id) REFERENCES site_user(user_id)
)ENGINE=MyISAM';
mysql_query($query,$db) or die(mysql_error($db));
echo 'all tables created';

