session_start();
include 'db.inc.php';

$db=mysql_connect(MYSQL_HOST,MYSQL_USER,MYSQL_PASSWORD) or die('unable to connect . ');

mysql_select_db(MYSQL_DB,$db) or die(mysql_error($db));

$username=(isset($_POST['username']))?trim($_POST['username']):'';
$password=(isset($_POST['password']))?trim($_POST['password']):'';

    
if(isset($_POST['submit']) && $_POST['submit'] == 'Login')
{
     $errors=array();
    
    if(empty($username))
    {
        $errors[]='Username cannot be blank';
    }
     if(empty($password))
    {
        $errors[]='password cannot be blank';
    }
    
    if(count($errors) >0)
    {
        echo'<p> Unable to process your registration due to following errors</p>';
        echo '<ul>';
        foreach($errors as $error)
        {
            echo'<li>'.$error.'</li>';
        }
        echo '</ul>';
    }
    else{
        $query='SELECT username,password FROM site_user WHERE username="'.$username.'" and password='.'PASSWORD("'.mysql_real_escape_string($password,$db).'")';
        $result=mysql_query($query,$db) or die(mysql_error($db));
        $rows =mysql_num_rows($result);
        if($rows > 0)
        {
            $_SESSION['logged']=1;
            echo 'You are being redirected to main page, please wait ';
            header('Refresh: 5; URL=index.php');
            die();
        }
        else{
            $_SESSION['logged']=0;
            echo '<strong>Invalid username and password . Try again</strong>';
        }
    }
}
